def minion_game(string):
    stuart_con = 0
    kevin_vow = 0

    for i, l in enumerate(s):
        length = len(s)
        if(s[i].upper() in 'AEIOU'):
            kevin_vow+=length-i
        else:
            stuart_con+=length-i

    if (kevin_vow > stuart_con):
        print('Kevin {}'.format(kevin_vow))
    elif (stuart_con > kevin_vow):
        print('Stuart {}'.format(stuart_con))
    else:
        print('Draw')

if __name__ == '__main__':
    # s = input()
    # s = 'BANANA'
    s = ''
    minion_game(s)
